/**
 * Given a directory and number of files, create
 * those files and return the number of successful
 * writes.
 **/
int createFiles( const char* dir, const char* sampleText, int numFiles ){
	int filesWritten = 0;
	int i = 0;
	int j = 0;
	int theFile = -1;
	// Path
	char fullPath[ gBufLarge ];
	char* fullPathPtr = fullPath;
	// Bytes to write
	char writeMe[ gBufLarge ];
	char* writeMePtr = writeMe;
	// The number as a string
	char chI[ gBufSmall ];
	char* chIPtr = chI;
	
	for( i = 0; i < numFiles; ++i ){
		memset( fullPathPtr, 0, gBufLarge );
		memset( writeMe, 0, gBufLarge );
		memset( chI, 0, gBufSmall );

		sprintf( chI, "%d", i );
		
		strcpy( fullPathPtr, dir );
		strcat( fullPathPtr, "/" );
		strcat( fullPathPtr, "file_" );
		strcat( fullPathPtr, chIPtr );
		strcat( fullPathPtr, ".txt" );
		strcpy( writeMePtr, sampleText );
		strcat( writeMePtr, fullPathPtr );
		
		theFile = open( fullPath, O_WRONLY | O_CREAT, 0644);
		if( theFile <= 0 ){
			//printf( "error: \"%s\" when opening \"%s\"\n", strerror( errno ), fullPathPtr );
		}
		
		size_t sizeWritten = write( theFile, writeMePtr, strlen( writeMe ) );
		if( sizeWritten > 0 ){
			filesWritten++;
		}else{
			//printf( "error: \"%s\" when writing \"%s\"\n", strerror( errno ), fullPathPtr );
		}
		close( theFile );	
	}
	
	//printf( "%d files were created and written to\n", filesWritten );
	return filesWritten;
}

